mapper.select:
SELECT * FROM ( SELECT CB.*, (SELECT COUNT(*) FROM CAFE_REPLY WHERE CAFE_BOARD_NO = CB.CAFE_BOARD_NO) REPLYCOUNT, ROW_NUMBER() OVER(ORDER BY CAFE_BOARD_NO DESC) AS RNUM FROM CAFE_BOARD CB WHERE CB.CAFE_MENU_NO = #{cafe_menu_no}
AND CB.TITLE LIKE '%' || #{keyword} || '%'
AND CB.CONTENT LIKE '%' || #{keyword} || '%'
AND CB.WRITER LIKE #{keyword}
AND CB.CONTENT LIKE '%' || #{keyword} || '%' OR CB.TITLE LIKE '%' || #{keyword} || '%'
) CAFE_BOARD
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}

SELECT CB.*, (SELECT COUNT(*) FROM CAFE_REPLY WHERE CAFE_BOARD_NO = CB.CAFE_BOARD_NO) REPLYCOUNT FROM CAFE_BOARD CB WHERE CB.CAFE_MENU_NO = #{cafe_menu_no}
AND CB.TITLE LIKE '%' || #{keyword} || '%'
AND CB.CONTENT LIKE '%' || #{keyword} || '%'
AND CB.WRITER LIKE #{keyword}
AND CB.CONTENT LIKE '%' || #{keyword} || '%' OR CB.TITLE LIKE '%' || #{keyword} || '%'

SELECT *
FROM(
   SELECT CR.*,
   ROW_NUMBER() OVER(ORDER BY CAFE_REPLY ) AS RNUM
   FROM CAFE_REPLY CR WHERE CAFE_BOARD_NO=#{cafe_board_no} 
) CAFE_REPLY
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}


SELECT *
FROM (
SELECT CB.*,
(SELECT COUNT(*) FROM CAFE_REPLY WHERE CAFE_BOARD_NO = CB.CAFE_BOARD_NO) AS REPLYCOUNT,
ROW_NUMBER() OVER(ORDER BY CAFE_BOARD_NO DESC) AS RNUM
FROM CAFE_BOARD CB WHERE CAFE_MENU_NO = #{cafe_menu_no}
) CAFE_BOARD
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}

SELECT *
FROM CAFE_REPLY WHERE CAFE_BOARD_NO=#{cafe_board_no} 
ORDER BY CAFE_REPLY


SELECT *
FROM CAFE_MEMBER
WHERE CAFE_NO = #{cafe_no}

mapper.update:
UPDATE CAFE_MEMBER
SET BLOCKYN = 'Y'
WHERE CAFE_MEMBER_NO=#{cafe_member_no}

UPDATE CAFE_MEMBER
SET BLOCKYN = 'N'
WHERE CAFE_MEMBER_NO=#{cafe_member_no}

UPDATE CAFE_MEMBER
SET SIGNYN = 'Y', SIGNDATE = SYSDATE
WHERE CAFE_MEMBER_NO=#{cafe_member_no}
